mapper.select:
select tn.notice_no , tn.notice_count , tn.notice_title , tn.reg_date , ui.name as writer , tn.is_delete from tb_notice tn join tb_userinfo ui on tn.loginID = ui.loginID
where tn.is_delete = 2
and tn.notice_title Like CONCAT('%', #{srctitle}, '%')
and tn.reg_date >= #{srcsdate}
and tn.reg_date <= #{srcedate}
order by tn.notice_no desc limit #{pageindex}, #{pageSize}

select count(1) as tot_cnt from tb_notice tn
where tn.is_delete = 2
and tn.notice_title Like CONCAT('%', #{srctitle}, '%')
and tn.reg_date >= #{srcsdate}
and tn.reg_date <= #{srcedate}

mapper.insert:

	select ifnull(max(notice_no), 0) + 1 from tb_notice

insert into tb_notice
(
notice_no
, loginID
, is_delete
, reg_date
,
notice_content
, notice_title
, notice_count
) values (
#{noticeno}
,
#{loginId}
, 2
, #{notice_datefile}
, #{notice_detfile}
,
#{notice_titlefile}
, 0
)

mapper.update:
update tb_notice
	set 
		  notice_title = #{notice_titlefile}
		, notice_content = #{notice_detfile}
		, update_date = date_format(Now(), '%Y-%m-%d')
		, file_cd = #{filecd}
where notice_no = #{noticeno}

update tb_file
	set
 		file_type = #{file_type}
,		file_size = #{file_size}
,		file_name = #{file_name}
,		file_nadd = #{file_nadd}
,		file_madd = #{file_madd}
where	file_cd = #{filecd}

mapper.insert:

	select ifnull(max(notice_no), 0) + 1 from tb_notice

insert into tb_notice
