mapper.insert:

	select ifnull(max(join_id), 0) + 1 from tb_fair_join

insert into tb_fair_join
(
join_id
, join_boothnum
, reg_date
, is_delete
,
fair_no
, loginID
) values (
#{joinid}
,
#{boothNum}
, date_format(Now(),
'%Y-%m-%d')
, 1
, #{id}
, #{loginId}
)

mapper.select:
SELECT join_boothnum AS boothNum
FROM tb_fair_join
WHERE fair_no = #{id}
AND join_boothnum IS NOT NULL AND is_delete IN (3);
