mapper.select:
SELECT tfa.loginID, tfj.join_id AS jid, tfa.fair_no AS id, tfa.fair_title AS title, ifnull(tfa.fair_startdate, '-') AS startDate, ifnull(tfa.fair_enddate, '-') AS endDate, ifnull(tdcLocation.detail_name, '-') AS location, ifnull(tdcLocation.detail_code, '0') AS locationCd, ifnull(tfa.fair_name, '-') AS name, ifnull(tfj.is_delete, '-') AS isDelete, ifnull(tdcReject.detail_name, '-') AS isDeleteName, ifnull(ui.user_phone, '-') AS phone FROM tb_fair tfa LEFT OUTER JOIN tb_userinfo ui ON tfa.loginID = ui.loginID LEFT OUTER JOIN tb_fair_join tfj ON tfa.fair_no = tfj.fair_no LEFT OUTER JOIN tb_detail_code tdcReject ON tdcReject.group_code='ad_yn' AND tfj.is_delete = tdcReject.detail_code LEFT OUTER JOIN tb_detail_code tdcLocation ON tdcLocation.group_code='location' AND tfa.fair_location = tdcLocation.detail_code
WHERE tfa.is_delete = 2 AND tfj.loginID = #{loginId}
and tfa.fair_title Like CONCAT('%', #{srctitle}, '%')
and tfa.fair_location Like CONCAT('%', #{region}, '%')
order by tfa.fair_no desc limit #{pageindex}, #{pageSize}

SELECT count(1) AS tot_cnt FROM tb_fair_join tfj LEFT OUTER JOIN tb_fair tfa ON tfa.fair_no = tfj.fair_no WHERE tfj.loginID = #{loginId}
and tfa.fair_title Like CONCAT('%', #{srctitle}, '%')
and tfa.fair_location Like CONCAT('%', #{region}, '%')
