mapper.select:
SELECT ta.ad_no , ta.ad_title , ta.ad_position , tdc.detail_name as ad_position_nm , ta.ad_type , tdd.detail_name as ad_type_nm , ta.ad_date FROM tb_ad ta LEFT JOIN tb_detail_code tdc ON tdc.detail_code = ta.ad_position AND tdc.group_code = 'position' LEFT JOIN tb_detail_code tdd ON tdd.detail_code = ta.ad_type AND tdd.group_code = 'ad_type' WHERE ta.loginID = #{loginID}
and ta.ad_title Like CONCAT('%', #{resumeAd_title}, '%')
and ta.reg_date >= #{start_date}
and ta.reg_date <= #{end_date}
and ta.is_delete = 2 and ta.ad_yn = 3 and ta.ad_status = 1 order by ta.ad_no desc limit #{pageIndex}, #{pageSize}

SELECT count(*) as cnt FROM tb_ad ta LEFT JOIN tb_detail_code tdc ON tdc.detail_code = ta.ad_position AND tdc.group_code = 'position' LEFT JOIN tb_detail_code tdd ON tdd.detail_code = ta.ad_type AND tdd.group_code = 'ad_type' WHERE ta.loginID = #{loginID}
and ta.ad_title Like CONCAT('%', #{resumeAd_title}, '%')
and ta.reg_date >= #{start_date}
and ta.reg_date <= #{end_date}
and ta.is_delete = 2 and ta.ad_yn = 3 and ta.ad_status = 1

mapper.update:
UPDATE tb_resume_submit SET
   submit_interviewdate = #{submit_interviewdate}
 , submit_interviewtime = #{submit_interviewtime}
 , interview_yn = 2
 WHERE submit_no = #{submit_no}
