mapper.select:
select tu.loginID, tu.name, ROUND((TO_DAYS(NOW()) - (TO_DAYS(tu.user_birthday))) / 365) AS age, tdcs.detail_name as sex, tdcp.detail_name as position, tdcl.detail_name as location, tu.is_delete from tb_userinfo tu left join tb_detail_code tdcp on tu.user_preferposition = tdcp.detail_code and tdcp.group_code= 'position' left join tb_detail_code tdcl on tu.user_preferarea = tdcl.detail_code and tdcl.group_code= 'location' left join tb_detail_code tdcs on tu.user_sexuality = tdcs.detail_code and tdcs.group_code= 'user_sexuality' where tu.user_type = 'C'
and tu.name Like CONCAT('%', #{nm_search}, '%')
limit #{pageindex}, #{pageSize}

select tu.loginID loginID2, tu.name name2, tdct.detail_name adtype, tdcex.detail_name adexp, tdced.detail_name adedu, tdcsa.detail_name adsal, ta.reg_date regdat, ta.ad_date addat, tdcst.detail_name adstat, tu.is_delete from tb_userinfo tu left join tb_ad ta on tu.loginID = ta.loginID left join tb_detail_code tdct on ta.ad_type = tdct.detail_code and tdct.group_code= 'ad_type' left join tb_detail_code tdcex on ta.ad_type = tdcex.detail_code and tdcex.group_code= 'resume_experience' left join tb_detail_code tdcsa on ta.ad_type = tdcsa.detail_code and tdcsa.group_code= 'resume_salary' left join tb_detail_code tdced on ta.ad_type = tdced.detail_code and tdced.group_code= 'education' left join tb_detail_code tdcst on ta.ad_type = tdcst.detail_code and tdcst.group_code= 'ad_status' where user_type = 'B'
and tu.name Like CONCAT('%', #{nm_search2}, '%')
limit #{pageindex}, #{pageSize}
