mapper.select:
select tu.loginID loginID2, tu.name name2, tdct.detail_name adtype, tdcex.detail_name adexp, tdced.detail_name adedu, tdcsa.detail_name adsal, ta.reg_date regdat, ta.ad_date addat, tdcst.detail_name adstat, tu.is_delete is_delete2 from tb_userinfo tu join tb_ad ta on tu.loginID = ta.loginID join tb_detail_code tdct on ta.ad_type = tdct.detail_code and tdct.group_code= 'ad_type' join tb_detail_code tdcex on ta.ad_type = tdcex.detail_code and tdcex.group_code= 'resume_experience' join tb_detail_code tdcsa on ta.ad_type = tdcsa.detail_code and tdcsa.group_code= 'resume_salary' join tb_detail_code tdced on ta.ad_type = tdced.detail_code and tdced.group_code= 'education' join tb_detail_code tdcst on ta.ad_type = tdcst.detail_code and tdcst.group_code= 'ad_status' where user_type = 'B'
and tu.name Like CONCAT('%', #{nm_search}, '%')
limit #{pageindex}, #{pageSize}

select tbf.fair_no fno, tbf.fair_title fttl, tdc.detail_name floc, tbf.fair_startdate fst, tbf.fair_enddate fed, tdc2.detail_name facn, tbf.fair_userNum faun from tb_fair tbf join tb_detail_code tdc on tbf.fair_location = tdc.detail_code and tdc.group_code = 'location' join tb_detail_code tdc2 on tbf.fair_companyNum = tdc2.detail_code and tdc2.group_code = 'fair_companyNum'
where tbf.fair_title Like CONCAT('%', #{nm_search2}, '%')
limit #{pageindex}, #{pageSize}
